// https://react.dev/learn/tutorial-tic-tac-toe
import { useState } from "react";

function Square({ value }) {
  return (
    <button className="square" onClick={onSquareClick}>
      {value}
    </button>
  );
}
  /* const [value, setValue] = useState(null); //value stores the value, and setValue is a function that can be used ot change the value
  function handleClick() { //a function that gives a click functionality
    setValue('X'); //when clicked sets a value of 'X'
  }
  return (
    <button
      className="square" 
      onClick={handleClick} //passes function when clicked
    >
      {value}
    </button>
  );
}*/

export default function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));

  function handleClick(i) {
    const nextSquares = squares.slice(); //creates a copy of the squres array(nextSquares) with the js slice array method, then handleclick updates the nextsquares array to x to the first([0] index) square
    nextSquares[i] = "X";
    setSquares(nextSquares);
  }
  return (
  <>
    <div className="board-row">
      <Square value={Square[0]} onSquareClick={() => handleClick(0)} />
      <Square value={Square[1]} onSquareClick={() => handleClick(1)} />
      <Square value={Square[2]} onSquareClick={() => handleClick(2)} />
    </div>
    <div className="board-row">
      <Square value={Square[3]} onSquareClick={() => handleClick(3)} />
      <Square value={Square[4]} onSquareClick={() => handleClick(4)}/>
      <Square value={Square[5]} onSquareClick={() => handleClick(5)}/>
    </div>
    <div className="board-row">
      <Square value={Square[6]} onSquareClick={() => handleClick} />
      <Square value={Square[7]} onSquareClick={handleClick} />
      <Square value={Square[8]} onSquareClick={handleClick} />
    </div>

  </>
  );
}
