// https://react.dev/learn/tutorial-tic-tac-toe
import { useState } from "react";

function Square() { //creates a hook
  const [value, setValue] = useState(null);
  function handleClick() {
    setValue('X'); //when clicked sets a value of 'X'
  }
  return (
    <button
      className="square" 
      onClick={handleClick}
    >
      {value}value
    </button>
  );
}

export default function Board() {
  const [squares,setSquares] = useState(Array(9).fill(null)); //creates an array with 9 elements and sets each of them to null while the use state() calls around it declares a square state variable thats set to that array, where each entry in the array corresponds to the value of a square.
  return (
  <>
    <div className="board-row">
      <Square />
      <Square />
      <Square />
    </div>
    <div className="board-row">
      <Square />
      <Square />
      <Square />
    </div>
    <div className="board-row">
      <Square />
      <Square />
      <Square />
    </div>

  </>
  );
}
