// https://react.dev/learn/tutorial-tic-tac-toe
import { useState } from "react";

function Square({value}) { //creates a hook

  // const [value, setValue] = useState(null); //value stores the value, and setValue is a function that can be used ot change the value
  function handleClick() { //a function that gives a click functionality
    setValue('X'); //when clicked sets a value of 'X'
  }
  return (
    <button
      className="square" 
      onClick={handleClick} //passes function when clicked
    >
      {value}
    </button>
  );
}

export default function Board() {
  const [squares,setSquares] = useState(Array(9).fill(null)); //creates an array with 9 elements and sets each of them to null while the use state() calls around it declares a square state variable thats set to that array, where each entry in the array corresponds to the value of a square.
  return (
  <>
    <div className="board-row">
      <Square value={Square[0]} />
      <Square value={Square[1]} />
      <Square value={Square[2]} />
    </div>
    <div className="board-row">
      <Square value={Square[3]} />
      <Square value={Square[4]} />
      <Square value={Square[5]} />
    </div>
    <div className="board-row">
      <Square value={Square[6]} />
      <Square value={Square[7]} />
      <Square value={Square[8]} />
    </div>

  </>
  );
}
